(11796, 4, '2021-05-03 23:52:14', '2021-05-03 23:52:14', '<!-- wp:heading {\"level\":3} -->\n<h3>Code Block</h3>\n<!-- /wp:heading -->\n\n<!-- wp:code -->\n<pre class=\"wp-block-code\"><code>.codeblock {\n    right: 100% !important;\n    position: absolute;\n}</code></pre>\n<!-- /wp:code -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Classic Block</h3>\n<!-- /wp:heading -->\n\n<p>This is the classic block.</p>\n<p>It\'s using the regular editor.</p>\n\n<!-- wp:heading {\"level\":3} -->\n<h3>HTML Block</h3>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<p style=\"font-weight:bold;font-size:16px;\">\n	My HTML block.\n</p>\n<!-- /wp:html -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Preformatted Block</h3>\n<!-- /wp:heading -->\n\n<!-- wp:preformatted -->\n<pre class=\"wp-block-preformatted\">This is a preformatted block.<br>It is the best block.<br>Amazing.</pre>\n<!-- /wp:preformatted -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Pullquote Block</h3>\n<!-- /wp:heading -->\n\n<!-- wp:pullquote -->\n<figure class=\"wp-block-pullquote\"><blockquote><p>This is a pullquote. It\'s pretty awesome.</p><cite>Laurel Fulford</cite></blockquote></figure>\n<!-- /wp:pullquote -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Table Block</h3>\n<!-- /wp:heading -->\n\n<!-- wp:table -->\n<figure class=\"wp-block-table\"><table><thead><tr><th>Header 1</th><th>Header 2</th><th>Header 3</th></tr></thead><tbody><tr><td>Bob</td><td>Smith</td><td>36</td></tr><tr><td>Jane</td><td>Doe</td><td>25</td></tr><tr><td>Susan</td><td>Smithers</td><td>34</td></tr></tbody></table></figure>\n<!-- /wp:table -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Verse Block</h3>\n<!-- /wp:heading -->\n\n<!-- wp:verse -->\n<pre class=\"wp-block-verse\">This is a verse.<br>It apparently has no styles? <br>Amazing.</pre>\n<!-- /wp:verse -->', 'Gutenberg: Formatting Blocks', '', 'inherit', 'closed', 'closed', '', '122-revision-v1', '', '', '2021-05-03 23:52:14', '2021-05-03 23:52:14', '', 122, 'http://localhost:8001/2021/05/